package Furi;

public class pixelData {
	// x value, y value, r, g, b, h, s, br, %signal
	public int x, y, r, g, b; 
	public float h, s, br; 
	public double signal; 
	
	public pixelData() {
		// TODO Auto-generated constructor stub
		x = 0; 
		y = 0;
		r = 0; 
		g = 0; 
		b = 0;
		h = 0; 
		s = 0;
		br = 0; 
		signal = 0.0; 
		
	}

}
